import React from "react";
import './Table.css';
import { useState } from "react";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
   
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {

    const [sortedItems, setSortedItems] = useState();

    function sortColumn(e: React.MouseEvent<HTMLElement>) {
        const cloneData = props.data.concat();
        if (e.target.inn === 'Word') {
            setSortedItems(cloneData.sort((a, b) => a.word < b.word ? -1 : 1))
        } else if (x === 'Translation') {
            setSortedItems(cloneData.sort((a, b) => a.translation < b.translation ? -1 : 1))
        } else if (x === 'Frequency') {
            setSortedItems(cloneData.sort((a, b) => a.frequency < b.frequency ? -1 : 1))
        } else if (x === 'Date') {
            setSortedItems(cloneData.sort((a, b) => a.translation < b.translation ? -1 : 1))
        }
      
}

    

    
    

    return (
        <table>
            <thead>
                <tr>
                    {props.columns.map((column, i) => {
                        return <th key={i} onClick={sortColumn}> {column.title}</th>;
                    })}
                </tr>
            </thead>
            {props.data.map((word, i) => {
                return (
                    <tbody key={i}>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })}
        </table>
    );
}


export default Table;
