import React from 'react';
import { Table} from './Table';
import { words, columnsWord } from './data';
import { useState } from 'react';
import { WordInfo } from './data';

const Component: React.FC = () => {
    const cloneData = words.concat();
    const [sortedItems, setSortedItems] = useState<WordInfo[]>([]);

    function onChangeValue(x: string) {
      
        if (x === 'Word') {
            setSortedItems(cloneData.sort((a,b)=>a.word<b.word?-1:1))
        } else if (x === 'Translation') {
            setSortedItems(cloneData.sort((a,b)=>a.translation<b.translation?-1:1))
        }
    }

    return (
        <div>
            <Table
                onChangeValue={onChangeValue}
                data={sortedItems}
                columns={columnsWord}
            />
               {/* <Table
                data={users}
                columns={columnsUser}
            /> */}
        </div>
    );
};

export default Component;
