import React from "react";
import './Table.css';
import { useState } from "react";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {
 
    // const [sortedProducts, setSortedProducts] = useState<T[]>(props.data)
    let sortableItems = [...props.data];

    function sortTable(e) {
      
     
         sortableItems.sort((a, b) => a.translation < b.translation ? -1 : 1)
        co
    
        }
     
        return (
            <table>
                <thead>
                    <tr>
                        {props.columns.map((column, i) => {
                            return <th key={i} onClick={(e) => sortTable(e)}> {column.title}</th>;
                        })}
                    </tr>
                </thead>
                {sortableItems.map((word,i) => {
                    return (
                        <tbody key={i}>
                            <tr>
                                {props.columns.map((column, i) => {
                                    return <td key={i}>{column.render(word)}</td>;
                                })}
                            </tr>
                        </tbody>

                    );
                })}
            </table>
        );
    }


export default Table;
