import React, { MouseEventHandler } from "react";
import './Table.css'

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {
    
    function sortTable(event:React.MouseEvent<HTMLElement>) {
    console.log(event.target)
}



    return (
        <table>
            <thead>
            <tr>
                {props.columns.map((column) => {
                    return <th onClick={sortTable}> {column.title}</th>;
                })}
            </tr>
            </thead>
         

            {props.data.map((word) => {
                return (
                    <tbody>
                        <tr>
                        {props.columns.map((column,) => {
                            return <td key={i}>{column.render(word)}</td>;
                        })}
                    </tr>
                    </tbody>
                    
                );
            })}
        </table>
    );
}

export default Table;
