import React from "react";
import './Table.css'

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {
    
    function sortTable(e:MouseEventHandler<HTMLTableCellElement>) {
    console.log(e)
}



    return (
        <table>
            <tr>
                {props.columns.map((column) => {
                    return <th onClick={sortTable}> {column.title}</th>;
                })}
            </tr>

            {props.data.map((word) => {
                return (
                    <tr>
                        {props.columns.map((column) => {
                            return <td>{column.render(word)}</td>;
                        })}
                    </tr>
                );
            })}
        </table>
    );
}

export default Table;
