import React from "react";
import './Table.css';
import { useState } from "react";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {

    const [sortedProducts, setSortedProducts] = useState([...props.data])

    function sortTable() {
       
setSortedProducts()


        return setSortedProducts(sortedProducts.sort((a: T, b: T) => a.word < b.word ? -1 : 1))
    }




    return (
        <table>
            <thead>
                <tr>

                    {props.columns.map((column, i) => {
                        return <th key={i} onClick={sortTable}> {column.title}</th>;
                    })}
                </tr>
            </thead>


            {sortedProducts.map((word) => {
                return (
                    <tbody>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })}
        </table>
    );
}


export default Table;
