import React from "react";
import './Table.css';
import { useState } from "react";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
    // sortingKey: keyof T;
    compare: (a: T, b: T) =>number;

 
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}

interface State<T> {
    key: keyof T,
    stateKey: boolean
}


export function Table<T>(props: PropsType<T>) {

    const [key, setKey] = useState<State<T> | null>(null);

  
     
    //     setKey((prev) => {
    //         if (prev !== null && prev.key === sortingKey) {
    //             return {
    //                 key: sortingKey,
    //                 stateKey: !prev.stateKey
    //             }
    //         } else {
    //             return {
    //                 key: sortingKey,
    //                 stateKey: true

    //             }
    //         }
    //     })
    // }

    const cloneData = props.data.concat();
    if (key !== null) {
        if (key.stateKey) {
            cloneData.sort((a, b) => a[key.key] < b[key.key] ? -1 : 1)
        } else {
            cloneData.sort(compare)
        }
    }
 
    return (
        <table>
            <thead>
                <tr>
                    {props.columns.map((column) => {
                        return <th key={column.title} onClick={() => sortColumn(column.compare)}> {column.title}</th>;
                    })}
                </tr>
            </thead>
            <tbody>
                {cloneData.map((word, i) => {
                    return (
                        <tr key={i}>
                            {props.columns.map((column) => {
                                return <td key={column.title}>{column.render(word)}</td>;
                            })}
                        </tr>

                    );
                })}
            </tbody>
        </table>
    );
}


export default Table;
