import React from "react";
import './Table.css';
import { useState } from "react";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {


    let sortedProducts = [...props.data];
    sortedProducts.sort((a, b) => {
      if (a.word < b.word) {
        return -1;
      }
      if (a.word > b.name) {
        return 1;
      }
      return 0;
    });

    // let sortableItems = [...props.data];
    // const [sortedProducts, setSortedProducts] = useState<T[]>(sortableItems)

    // function sortTable(e) {

    //     setSortedProducts((prev) => {
    //         console.log(prev)
    //         console.log(sortedProducts)
    //         return prev.sort((a, b) => a.word <b.word?- 1: 1)
    //     })
    //     // sortableItems.sort((a, b) => a.translation < b.translation ? -1 : 1)
    //     // console.log(sortableItems)

    // }
    // console.log(sortedProducts)
    return (
        <table>
            <thead>
                <tr>
                    {props.columns.map((column, i) => {
                        return <th key={i} onClick={(e) => sortTable(e)}> {column.title}</th>;
                    })}
                </tr>
            </thead>
            {sortedProducts.map((word, i) => {
                return (
                    <tbody key={i}>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })}
        </table>
    );
}


export default Table;
