import React from "react";
import './Table.css'
import { WordInfo } from "./data";

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {


    
    function sortTable(event:React.MouseEvent<HTMLElement>) {
      
       return props.data.sort((n1,n2) => {
            if (n1.word > n2.word) {
                return 1;
            }
        
            if (n1.word < n2.word) {
                return -1;
            }
        
            return 0;
        });
    }
    
    // console.log(sortTable())

    return (
        <table>
            <thead>
            <tr>
                {props.columns.map((column,i) => {
                    return <th key={i} onClick={sortTable}> {column.title}</th>;
                })}
            </tr>
            </thead>
         

            {sortTable().map((word) => {
                return (
                    <tbody>
                        <tr>
                        {props.columns.map((column,i) => {
                            return <td key={i}>{column.render(word)}</td>;
                        })}
                    </tr>
                    </tbody>
                    
                );
            })}
        </table>
    );
}

export default Table;
