import React from 'react';
import { Table} from './Table';
import { words, columnsWord } from './data';
import { useState } from 'react';
import { WordInfo } from './data';

const Component: React.FC = () => {
    
    const [sortedItems, setSortedItems] = useState([...words]);

    function onChangeValue(x: string) {
     console.log(x)
        if (x === 'Word') {
            setSortedItems((prev) => {
                return prev.sort((a,b)=>a.word<b.word?-1:1)
            })
        } else if (x === 'Translation') {
            setSortedItems((prev) => {
                return prev.sort((a,b)=>a.translation<b.translation?-1:1)
            })
        }
    }

    return (
        <div>
            <Table
                onChangeValue={onChangeValue}
                data={sortedItems}
                columns={columnsWord}
            />
               {/* <Table
                data={users}
                columns={columnsUser}
            /> */}
        </div>
    );
};

export default Component;
