import React from "react";
import './Table.css';

export interface Column<T> {
    title: string;
    render: (w: T) => string;
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
}


export function Table<T>(props: PropsType<T>) {

    let a = [];

    function sortTable(event: React.MouseEvent<HTMLElement>, p: T[]) {
        a = p.sort((a: T, b: T) => a.word < b.word ? -1 : 1);
        return a
    }

    console.log(a)

    return (
        <table>
            <thead>
                <tr>
               
                    {props.columns.map((column, i) => {
                        return <th key={i} onClick={(e) => sortTable(e, props.data)}> {column.title}</th>;
                    })}
                </tr>
            </thead>

            { a.length?a.map((word) => {
                return (
                    <tbody>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            }) :
            props.data.map((word) => {
                return (
                    <tbody>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })}
            
            {/* {props.data.map((word) => {
                return (
                    <tbody>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })} */}
        </table>
    );
}

export default Table;
