import React from "react";
import './Table.css';
import { useState } from "react";



export interface Column<T> {
    title: string;
    render: (w: T) => string;
   
}

interface PropsType<T> {
    data: T[];
    columns: Column<T>[];
    onChangeValue: (x:string) => void;
}


export function Table<T>(props: PropsType<T>) {

    const [a,setA] = useState<T[]|undefined>()

    function handle(e:any) {
        props.onChangeValue(e.target.innerText)
        setA(props.data)
    
}

   

    return (
        <table>
            <thead>
                <tr>
                    {props.columns.map((column, i) => {
                        return <th key={i} onClick={handle}> {column.title}</th>;
                    })}
                </tr>
            </thead>
            {a.map((word, i) => {
                return (
                    <tbody key={i}>
                        <tr>
                            {props.columns.map((column, i) => {
                                return <td key={i}>{column.render(word)}</td>;
                            })}
                        </tr>
                    </tbody>

                );
            })}
        </table>
    );
}


export default Table;
